import React from 'react'

export default class Booking extends React.Component {
    constructor() {
        super();
        this.state = { showTickets: false, ticketId : null, booking: {
          bookingId: null, price: null, tickets : []
        }}
    }


    generateButtons = () => {

    }

    render() {
        return (<div>ids:{this.state.booking.tickets[0].flightId}
            <form onSubmit={this.showTickets}>
                <input placeholder="booking id" />
                <input type="submit" />
            </form>
        {this.state.showTickets ? <form onSubmit={this.doneWithBooking}>
      <p>Select a ticket:</p>

      <ul>
        <li>
          <label>
            <input
              type="radio"
              value="1"
              checked={this.state.size === "small"}
              onChange={this.handleChange}
            />
            ticket1 (id 1)
          </label>
        </li>

        <li>
          <label>
            <input
              type="radio"
              value="1"
              checked={this.state.size === "medium"}
              onChange={this.handleChange}
            />
            ticket2 (id 1)
          </label>
        </li>

        <li>
          <label>
            <input
              type="radio"
              value="1"
              checked={this.state.size === "large"}
              onChange={this.handleChange}
            />
            ticket3 (id 1)
          </label>
        </li>
      </ul>

      <button type="submit">Make your choice</button>
        <p>currently picked: {this.state.ticketId}</p>
    </form>:'please pick an id'}



        </div>)
    }
    handleChange = (event) => {
        this.setState({
          ticketId: event.target.value
        });
      }

    showTickets = (e) => {
        this.setState({showTickets: true});
        fetch("http://localhost:5009/api/v1/food/bookings?id=" + e.target[0].value, {
          method: "get",
          headers: {'Content-Type': 'application/json' }
        }).then((res)=>{
          return res.json();
        }).then((json)=>{
          var arr= [];
          for (let index = 0; index < json.booking.tickets.length; index++) {
            arr.push(json.booking.tickets[index].flightId);
          }
          alert(arr);
          this.setState({booking : json});
        })
        this.props.bookingDone("booking id", e.target[0].value);
        e.preventDefault();
    }

    doneWithBooking = (e) => {
        this.props.bookingDone("ticket id", this.state.ticketId);
        this.props.bookingDone("booking done");
        e.preventDefault();
    }
}
