import React from 'react';
import Booking from './Booking'
import Food from './Food'
import Feedback from './Feedback'

class Root extends React.Component{

  constructor(){
    super();
    this.state = {doneWithBooking: false, doneWithFood: false, doneWithFeedback: false}
  }
  render(){
    return(<div>
      {this.serviceStepsChecker()}
    </div>)
  }

  serviceStepsChecker = () => {
    if(this.state.doneWithBooking === false){
      return <Booking done={this.state.doneWithBooking}/>
    }
    if(this.state.doneWithBooking === true && this.state.doneWithFood === false){
      return <Food done={this.state.doneWithFood}/>
    }
    if(this.state.doneWithBooking === true && this.state.doneWithFood === true){
      return <Feedback done={this.state.doneWithFeedback}/>
    }
  }
  changeState = (choice) => {
    if(choice === "booking done"){
      this.setState({doneWithBooking: true})
    }
    if(choice === "food done"){
      this.setState({doneWithFood: true})
    }
    if(choice === "feedback done"){
      this.setState({doneWithFeedback: true})
    }
  }
}



export default Root;
